        {/*</p>*/}


        {/*<a*/}
        {/*  className="App-link"*/}
        {/*  href="https://reactjs.org"*/}
        {/*  target="_blank"*/}
        {/*  rel="noopener noreferrer"*/}
        {/*>*/}
        {/*  Learn React*/}
        {/*</a>*/}
      </header>
        <div>

            <hr/>
            <hr/>
            <MoviesList />
            <hr/>
            <hr/>
            <MoviesByYear />
            <hr/>
            <hr/>
            <PopularYears />
        </div>
    </div>
  );
}

export default App;
